package format;

/**
 * @author cryston.heaven
 *
 */
public class Prefix_Postfix {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int a = 6;
		int b = 6;

		System.out.println("A is "+a++);//Increase after the output
		
		System.out.println("now is now "+a++);//Increase after the output
		
		System.out.println("B is "+ ++b);//Increase before the output
		
		System.out.println("B is "+ ++b);//Increase before the output
		
		//System.out.println(a);
		//System.out.println(a);

	}

}
